import React from 'react';
import "../../css/Components/header.css"

 class Header extends React.Component {
    
    constructor(props) {
      super(props)
      this.state={

      }
    }

    handleOpenMenu = (e) => {
      console.log(e.target);
      if (e.target.id !== "hamb"){
        e.target = e.target.parentNode;
      }
      for (let i = 0 ; i< e.target.childElementCount ; i++){
        e.target.childNodes[i].className = "animationHamb"
      }

      //this.props.handleOpenMenu();
    }


    render() {
      return (
            <div id="header">
                <div id="headerBlock">
                    <div id="logoBlock">
                      <img id="logo" src='img\Logo.png' alt="logo"/>
                    </div>
                    <div id="titleBlock">
                      <p id="titleTxt">Le Jeu de Piste</p>
                    </div>
                    <div id="hambBlock">
                      <div id="hamb" onClick={this.handleOpenMenu}>
                        <div id="hambUp"></div>
                        <div id="hambMid"></div>
                        <div id="hambDown"></div>
                      </div>
                    </div>
                </div>
            </div>
      )
    }
  }

  export default Header
