import React from 'react';
import "../../css/Components/button.css"

 class Button extends React.Component {

    constructor(props){
        super(props)
        this.state = {
            color:this.props.color,
            borderColor:this.props.borderColor
        }
    }

    componentDidMount(){
        this.setColor()
        this.setBorderColor()
    }

    setColor = () => {
        var element = document.getElementById("button");
        switch (this.state.color) {
            case "blue" : 
                element.style.cssText += "background: linear-gradient(180deg, #091688 0%, #0A0D21 100%);"
                break;
            case "blueInv" : 
                element.style.cssText += "background: linear-gradient(180deg, #0A0D21 0%, #091688 100%);"
                break;
            case "red" :
                element.style.cssText += "background: linear-gradient(180deg, #C41515 0%, #2D0E0E 100%);"
                break;
            case "redInv" :
                element.style.cssText += "background: linear-gradient(180deg, #2D0E0E 0%, #C41515 100%);"
                break;
            default:
                break;
        }
    }

    setBorderColor = () => {
        var element = document.getElementById("button");
        switch (this.state.borderColor){
            case "white" :
                element.style.cssText += "border: 1vw solid #000000,box-sizing: border-box,filter: drop-shadow(-1vw 1vw 1vw rgba(0, 0, 0, 0.75))"
                break;
            case "black" :
                element.style.cssText += "border: 1vw solid #ffffff,box-sizing: border-box,filter: drop-shadow(-1vw 1vw 1vw rgba(255, 255, 255, 0.75))"
                break;
            default :
                break;
        }
    }


    render() {
      return (
            <div id="button">
                <p id="buttonTxt">{this.props.title}</p>
            </div>
      )
    }
  }

  export default Button
