import React from 'react';
import "../../css/Components/button.css"

 class Button extends React.Component {

    constructor(props){
        super(props)
        this.state = {
            color:this.props.color,
            borderColor:this.props.borderColor,
            style:""
        }
    }

    componentDidMount(){
    }

    setColor = () => {
            let color = ""
            switch (this.state.color) {
                case "blue" : 
                    color = "linear-gradient(180deg, #091688 0%, #0A0D21 100%)"
                    break;
                case "blueInv" : 
                    color = "linear-gradient(180deg, #0A0D21 0%, #091688 100%)"
                    break;
                case "red" :
                    color = "linear-gradient(180deg, #C41515 0%, #2D0E0E 100%)"
                    break;
                case "redInv" :
                    color = "linear-gradient(180deg, #2D0E0E 0%, #C41515 100%)"
                    break;
                default:
                    break;
            }
            return(color)
        }

    setBorderColor = () => {

        let borderColor= {
            border:"",
            filter:""
        };

            switch (this.state.borderColor){
                case "black" :
                    borderColor.border = "1vw solid #000000;"
                    borderColor.filter = "drop-shadow(-1vw 1vw 1vw rgba(0, 0, 0, 0.75));"
                    break;
                case "white" :
                    borderColor.border = "0.5vw solid #ffffff;"
                    borderColor.filter = "drop-shadow(-1vw 1vw 1vw rgba(255, 255, 255, 0.75));"
                    break;
                default :
                    break;
            }

        return(borderColor)
    }


    render() {
      return (
            <div className="button" style={{background:this.setColor(),border:this.setBorderColor().border,filter:this.setBorderColor().filter}}>
                <p id="buttonTxt">{this.props.title}</p>
            </div>
      )
    }
  }

  export default Button
