import React from 'react';
import "../../css/Views/enigma.css"
import Footer from '../Components/Footer';
import Header from '../Components/Header';

 class Enigma extends React.Component {

    constructor(props){
        super(props)
        this.state={

        }
    }

    componentDidMount(){
        this.animateBack()
    }

    animateBack(){
        let back = document.getElementById("back");
        back.className = "animBack"
        let head = document.getElementById("header");
        head.className = "animHead"
        let foot = document.getElementById("footer");
        foot.className = "animFoot"
        let title = document.getElementById("transpBlockTitle");
        title.className = "animBoxTitle"
        let enigma = document.getElementById("transpBlockEnigma");
        enigma.className = "animEnigma"
        let littleChar = document.getElementById("littleCharBox");
        littleChar.className = "animChar"
        let bulleTriangle = document.getElementById("bulleTriangleBlock");
        let bulleBlock = document.getElementById("bulleBlock")
        bulleTriangle.className = "animTriangle"
        bulleBlock.className = "animBulle"
    }

    render() {
      return (
        <div id="enigma">
        <div id="back"></div>
        <Header/>
        <div id="transpBlockTitle">
            <div id="transpBoxTitle">
                <p id="titleEnigmaTxt">Enigme 1</p>
            </div>
        </div>
        <div id="transpBlockEnigma">
                <div id="transpBoxEnigma"></div>
        </div>
        <div id="littleCharBlock">
            <div id="littleCharBox">

            </div>
            <div id="bulleTriangleBlock">
                <img id="bulleTriangle" src='img\Triangle.png' alt="triangle"/>
            </div>
            <div id="bulleBlock">
                <div id="bulleBox">
                    <p id="bulleTxt">Coucou, moi c’est petit perso ! Je vais t’aider dans cette aventure. Mais tu dois m’aider moi aussi !</p>
                    <div id="bulleTapBlock">
                        <img id="bulleTap" src='img\Lecture.png' alt='tap'/>
                    </div>
                </div>
            </div>
        </div>
        <Footer/>
        </div>
      )
    }
  }

  export default Enigma
