import React from 'react';
import "../../css/Components/button.css"

 class Button extends React.Component {

    constructor(props){
        super(props)
        this.state = {
            color:this.props.color
        }
    }

    componentDidMount(){
        this.setColor()
    }

    setColor = () => {
        let element = document.getElementById("button");
        switch (this.state.color) {
            case "blue" : 
                element.style.cssText = "background: linear-gradient(180deg, #091688 0%, #0A0D21 100%);"
                break;
            case "blueInv" : 
                element.style.cssText = "background: linear-gradient(180deg, #0A0D21 0%, #091688 100%);"
                break;
            case "red" :
                element.style.cssText = "background: linear-gradient(180deg, #C41515 0%, #2D0E0E 100%);"
                break;
            case "redInv" :
                element.style.cssText = "background: linear-gradient(180deg, #2D0E0E 0%, #C41515 100%);"
                break;
            default:
                break;
        }
    }

    invColor = () => {
        this.setState({color:this.state.color+"Inv"},()=>{
            this.setColor();
            console.log(this.state.color);
        })
    }

    deInvColor = () => {
        this.setState({color:this.props.color},()=>{
            this.setColor();
            console.log(this.state.color);
        })
    }

    render() {
      return (
            <div id="button" onClick={this.setState({color:""})}>
                <p id="buttonTxt">{this.props.title}</p>
            </div>
      )
    }
  }

  export default Button
