import React from 'react';
import { Link } from 'react-router-dom';
import "../../css/Components/button.css"

 class Button extends React.Component {

    constructor(props){
        super(props)
        this.state = {
            color:this.props.color,
            borderColor:this.props.borderColor,
            current_borderColor:{},
            current_color:"",
            style:""
        }
    }

    componentDidMount(){
        this.setColor()
        this.setBorderColor()
    }

    setColor = () => {
            let color = ""
            switch (this.state.color) {
                case "blue" : 
                    color = "linear-gradient(180deg, #091688 0%, #0A0D21 100%)"
                    break;
                case "red" :
                    color = "linear-gradient(180deg, #C41515 0%, #2D0E0E 100%)"
                    break;
                case "green" :
                    color = "linear-gradient(180deg, #1F682F 0%, #07160A 100%)"
                    break;
                default:
                    break;
            }
            
            this.setState({current_color:color})
        }

    setBorderColor = () => {

        let borderColor= {
            border:"",
            filter:""
        };

            switch (this.state.borderColor){
                case "black" :
                    borderColor.border = "1vw solid #000000"
                    borderColor.filter = "drop-shadow(-1vw 1vw 1vw rgba(0, 0, 0, 0.75))"
                    break;
                case "white" :
                    borderColor.border = "0.5vw solid #ffffff"
                    borderColor.filter = "drop-shadow(-1vw 1vw 1vw rgba(255, 255, 255, 0.75))"
                    break;
                default :
                    break;
            }

        this.setState({current_borderColor:borderColor})
    }

    changeGradient = () => {
        console.log("drag");
        // mettre un nouveau nom de classe et mettre :active dessus
       
    }


    render() {
      return (
            this.props.linkto ? 
            <Link id="link" to={this.props.linkto}>
                <div className="button"  style={{background:this.state.current_color,border:this.state.current_borderColor.border,filter:this.state.current_borderColor.filter}}>
                    <p id="buttonTxt">{this.props.title}</p>
                </div>
            </Link>
            :
            <g onMouseDown={this.changeGradient} className="button"  style={{background:this.state.current_color,border:this.state.current_borderColor.border,filter:this.state.current_borderColor.filter}}>
                <p id="buttonTxt">{this.props.title}</p>
            </g>
            
      )
  }

 }

  export default Button
