import React from 'react';
import "../../css/Views/home.css"
import Button from '../Components/Button';
import Footer from '../Components/Footer';
import Header from '../Components/Header';
import Menu from '../Components/Menu';

 class Home extends React.Component {

    constructor(props){
        super(props)
        this.state={
            onMenu:false
        }
    }

    handleOpenMenu = () => {
        this.setState({onMenu:true})
    }

    handleCloseMenu = () => {
        let menu = document.getElementById("menu")
        menu.className = "animMenuInv"
        console.log(menu);
        this.setState({onMenu:false})
    }


    render() {
      return (
        <div id="home">
            <Header handleOpenMenu={this.handleOpenMenu} handleCloseAbove={this.handleCloseMenu}/>
            { this.state.onMenu &&
            <Menu/>
            }
            <div id="transpBlock">
                <div id="transpBox">
                </div>
            </div>
            <div id="buttonBlock">
                <Button title="Commencer" color="blue" borderColor="black"/>
            </div>
            <Footer/>
        </div>
      )
    }
  }

  export default Home
